import React, { Component } from 'react';
// import products from '../../data/products';
import ProductCard from '../../components/ProductCard';
import logo_round from '../../assets/img/logo_round.svg'

import { APIHandler } from '../../config/C4';

class HomeView extends Component {
  constructor() {
    super();
    this.state = {
      products: [],
    }
    this.getProducts = this.getProducts.bind(this);
  }
  componentDidMount() {
    this.getProducts();
  }

  async getProducts() {
    const products = await APIHandler.getProducts();
    this.setState({ products });
  }

  render() {
    const { products } = this.state;
    console.log(products);
    return (
      <React.Fragment>
        <header className="header">
          <div className="header__text">
            <h1>For that extra little <span>spark</span> in your life</h1>
            <button className="btn">Shop now</button>
          </div>
          <img className="logo-round" src={logo_round} alt="" />
        </header>
        <div className="homeview__grid">
          {products && products.length > 0 && products.map((item, i) => {
            return (
              <img src={item.image} alt=""/>
            //   <ProductCard
            //     key={i} 
            //     item={item}
            //   />
            )
          })}
        </div>
      </React.Fragment>
    );
  }
}

export default HomeView;
