import React, { Component } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTruck } from '@fortawesome/free-solid-svg-icons';
import { faMessage, faHeart } from '@fortawesome/free-regular-svg-icons';
import { Link } from 'react-router-dom';

import { products, ProductCard } from '../../config/C4';
import logoRound from '../../assets/img/logo/sticker_logo_blue.svg'

class HomeView extends Component {
  render() {
    return (
      <div className="homeView">
        <header className="homeView__header">
          <div className="homeView__header--text">
            <h1>For that extra little <span>spark</span> in your life</h1>
            <Link to="/shop" className="btn">Shop now</Link>
          </div>
          <img className="homeView__header--logo-round" src={logoRound} alt="" />
        </header>
        <div className="homeView__usp">
          <Link to="/shipping" className="homeView__usp--item">
            <FontAwesomeIcon icon={faTruck} />
            <p><span>Free shipping</span> on all orders over â‚¬30,-</p>
          </Link>
          <Link to="/reviews" className="homeView__usp--item">
            <FontAwesomeIcon icon={faMessage} />
            <p>Average rating of <span>4.5 stars</span></p>
          </Link>
          <Link to="/build-your-own-candle" className="homeView__usp--item">
            <FontAwesomeIcon icon={faHeart} />
            <p>Design and build <span>your own candle</span></p>
          </Link>
        </div>
        <div className="homeView__grid">
          {products.map((item, i) => {
            return (
              <ProductCard
                key={i} 
                item={item}
              />
            )
          })}
        </div>
      </div>
    );
  }
}

export default HomeView;
