import React from 'react';
import { Image, PriceHandler, products, Stars } from '../../config/C4';

const ProductView = () => {
  const { pathname } = window.location;
  const slug = pathname.split('/product/')[1];
  const product = products.filter(i => i.url === slug)[0];
  const { 
    // type,
    title,
    // url,
    // id,
    price,
    // fragrance,
    // measurements,
    capacity,
    // color,
    // collection,
    featured_image,
    // images,
    rating: {
      stars,
      amount_of_reviews
    },
    additionalInfo: {
      short,
      description,
      // ingredients,
    }
  } = product;

  return (
    <div className="productView">
      <div className="productView__info">
        <Image 
          src={`products/${featured_image}`}
          alt={title}
          className="productView__info--image"
        />
        <div className="productView__info--text">
          <h1>{title}</h1>
          <h2>{short}</h2>
          <div className="rating">
            <Stars
              rating={stars}
              reviewsAmount={amount_of_reviews}
            />
          </div>
          <div className="pricing">
            {PriceHandler(price)}
            <div className="pricing__seperator"></div>
            <p className="pricing__capacity">{capacity}</p>
          </div>
          <div className="description">
            {description.map((d, i) => (
              <p key={i}>{d}</p>
            ))}
          </div>
        </div>
      </div>
    </div>
  )
}

export default ProductView;
