import React from 'react';
import { Image, PriceHandler, useCartHandler} from '../../config/C4';

const CartDrawerProduct = (props) => {
    const { ChangeAmountInCartHandler, RemoveCartItem } = useCartHandler();
    const { item } = props;
    const { product, amount } = item;
    const { id, title, price, additionalInfo: { short }, featured_image } = product[0];
    return (
        <div className="cart-drawer-product">
            <Image
              src={`products/${featured_image}`}
              alt={title}
              className="cart-drawer-product__image"
            />
            <div className="cart-drawer-product__text">
                <div className="cart-drawer-product__text--info">
                    <h2>{title}</h2>
                    <p>{short}</p>
                </div>
                <div className="cart-drawer-product__text--price">
                    <PriceHandler price={price} />
                </div>
                <div className="cart-drawer-product__text--amount">
                    <div className="amount-changer">
                        <button 
                            className="remove"
                            onClick={() => ChangeAmountInCartHandler(id, -1)}
                        >
                            <span className="material-icons">remove</span>
                        </button>
                        <div className="current-amount">{amount}</div>
                        <button 
                            className="add"
                            onClick={() => ChangeAmountInCartHandler(id, 1)}
                        >
                            <span className="material-icons">add</span>
                        </button>
                    </div>
                    <button
                        type="button"
                        className="remove-all"
                        onClick={() => RemoveCartItem(id)}
                    >
                        Remove
                    </button>
                </div>
            </div>
        </div>
    );
}

export default CartDrawerProduct;
