import React, { useContext, useEffect, useState } from "react";
import { CartContext } from "../../contexts/CartContext";
import products from "../../data/products";
import CartDrawerProduct from "../CartDrawerProduct";
import { GetCartItems } from "../../config/C4";
import { Link } from "react-router-dom";
// import PriceHandler from '../../config/PriceHandler';

const CartDrawer = (props) => {
  const { toggleCartDrawer } = props;
  const { cartItems } = useContext(CartContext);
  const [totalProductsInCart, setTotalProductsInCart] = useState(null);
  const [fullCartItems, setFullCartItems] = useState([]);

  useEffect(() => {
    if (cartItems && cartItems.length > 0) {
      const total = cartItems.reduce((a, b) => ({
        amount: a.amount + b.amount,
      }));
      setTotalProductsInCart(total.amount);
      const fullProducts = cartItems.map((item) => {
        const product = products.filter((product) => {
          return product.id === item.id;
        });
        return { product: product, amount: item.amount };
      });
      setFullCartItems(fullProducts);
    }
  }, [cartItems]);

  return (
    <div className="cart-drawer">
      <div className="cart-drawer__background" />
      <div className="cart-drawer__content">
        <div className="cart-drawer__content--header">
          <button type="button" className="closeBtn" onClick={toggleCartDrawer}>
            <span className="material-icons">close</span>
          </button>
          <h1>
            Your Bag (
            {cartItems && totalProductsInCart > 0 ? totalProductsInCart : 0})
          </h1>
          {fullCartItems.length > 0 && <h2>Your items are shipping for free!</h2>}
        </div>
        <div className="cart-drawer__content--products">
          {fullCartItems.length > 0 ? (
            fullCartItems.map((item, i) => {
              return <CartDrawerProduct key={i} item={item} />;
            })
          ) : (
            <div className="empty-cart">
              <h1>Your bag is currently empty!</h1>
              <p>Visit the shop to find what you're looking for.</p>
              <Link className="btn" to="/shop">Let's go shopping</Link>
            </div>
          )}
        </div>
        {cartItems.length > 0 && (
          <div className="cart-drawer__content--total">
            <span>
              <h1>Subtotal</h1>
              {/* <h1>{PriceHandler(totalPrice)}</h1> */}
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartDrawer;
