import React, { useState } from "react";
import { CartHandler } from "../../config/C4";

const AmountChanger = ({ id, amount }) => {
  const { edit, remove } = CartHandler();
  const [newAmount, setNewAmount] = useState(amount);

  const handleAmountChange = (operation) => {
    if (operation === '-') {
      if (newAmount <= 0) {
        return;
      }

      edit(id, '-')
      setNewAmount(newAmount - 1);
    }
    
    if (operation === '+') {
      edit(id, '+')
      setNewAmount(newAmount + 1);
    }
  }

  return (
    <React.Fragment>
      <div className="amount-changer">
        <button
          className="remove"
          onClick={() => handleAmountChange('-')}
        >
          <span className="material-icons">remove</span>
        </button>
        <div className="current-amount">{newAmount}</div>
        <button
          className="add"
          onClick={() =>  handleAmountChange('+')}
        >
          <span className="material-icons">add</span>
        </button>
      </div>
      <button
        type="button"
        className="remove-all"
        onClick={() => remove(id)}
      >
        Remove
      </button>
    </React.Fragment>
  );
};

export default AmountChanger;
