import React from 'react';
import { Link } from 'react-router-dom';
import { Image, PriceHandler, CartHandler } from '../../config/C4';

const ProductCard = (props) => {
    const { add } = CartHandler();
    const { item } = props;
    const { title, id, price, additionalInfo: { short }, thumbnail, url } = item;

     return (
        <div className="product-card">
            <Link to={`/product/${url}`}><Image src={`products/${thumbnail}`} alt={title} /></Link>
            <div className="product-card__text">
                <Link to={`/product/${url}`}><h1>{title}</h1></Link>
                <p>{short}</p>
            </div>
            
            <PriceHandler price={price} />
            <button className="btn add-to-cart" onClick={() => add(id)}>Add to cart</button>
        </div>
    )
}

export default ProductCard;
