import React from "react";
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import { products } from "../../config/C4";

const MenuItems = ({ prefix }) => {
  const collections = [];
  products.map((i) => {
    if (!collections.includes(i.collection)) {
      collections.push(i.collection);
    }
    return null;
  });

  <div class="navbar">
    <a href="#home">Home</a>
    <a href="#news">News</a>
    <div class="dropdown">
      <button class="dropbtn">
        Dropdown
        <i class="fa fa-caret-down"></i>
      </button>
      <div class="dropdown-content">
        <div class="header">
          <h2>Mega Menu</h2>
        </div>
        <div class="row">
          <div class="column">
            <h3>Category 1</h3>
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
          <div class="column">
            <h3>Category 2</h3>
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
          <div class="column">
            <h3>Category 3</h3>
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
        </div>
      </div>
    </div>
  </div>;

  return (
    <div className={`${prefix}menu-items`}>
      <Link to="/shop" className="dropdown">
        Shop <FontAwesomeIcon icon={faChevronDown} />
        <div className="dropdownbox">
          <div className="dropdownbox__content">
            <div className="dropdownbox__content">
              <h1>Collections</h1>
              <div className="dropdownbox__content--collections">
                {collections &&
                  collections.map((item, i) => (
                    <div
                      to={`category/${item}`}
                      key={i}
                    >
                      {item}
                    </div>
                  ))}
              </div>
              <h1>
                <Link to="/products">Show all candles</Link>
              </h1>
            </div>
          </div>
        </div>
      </Link>
      <Link to="/our-story">Our Story</Link>
      <Link to="/build-your-own-candle" className="new">
        Build your own candle
      </Link>
    </div>
  );
};

export default MenuItems;
