import React, { useState } from "react";
import { useCartHandler } from "../../config/C4";

const AmountChanger = ({ id, amount }) => {
  const { ChangeAmountInCartHandler, RemoveCartItem } = useCartHandler();
  const [newAmount, setNewAmount] = useState(amount);

  return (
    <React.Fragment>
      <div className="amount-changer">
        <button
          className="remove"
          onClick={() => {
            ChangeAmountInCartHandler(id, -1)
            setNewAmount(newAmount - 1);
          }}
        >
          <span className="material-icons">remove</span>
        </button>
        <div className="current-amount">{amount}</div>
        <button
          className="add"
          onClick={() => {
            ChangeAmountInCartHandler(id, 1);
            setNewAmount(newAmount + 1);
          }}
        >
          <span className="material-icons">add</span>
        </button>
      </div>
      <button
        type="button"
        className="remove-all"
        onClick={() => RemoveCartItem(id)}
      >
        Remove
      </button>
    </React.Fragment>
  );
};

export default AmountChanger;
