import React, {useState} from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faStarHalfStroke } from '@fortawesome/free-solid-svg-icons';
import { faStar as faStarOutlined } from '@fortawesome/free-regular-svg-icons';

const Stars = ({ rating, reviewsAmount }) => {
  const [fullStars, setFullStars] = useState(null);
  const [halfStars, setHalfStars] = useState(false);
  const [emptyStars, setEmptyStars] = useState(null);

  const calculateAmountOfStars = () => {
    const stars = parseInt(rating);
    const starsDecimal = parseFloat(rating) - Math.floor(parseFloat(rating));
    const starsDecimalFixed = parseFloat(starsDecimal.toFixed(1));

    if (!fullStars) {
      setFullStars(stars);
    }

    if (starsDecimalFixed >= 0.3 && starsDecimalFixed < 0.8 && !halfStars) {
      setHalfStars(true);
    }

    if (halfStars && fullStars < 4 && !emptyStars) {
      const amountOfEmptyStars = 5 - 1 - fullStars;
      console.log('test 1');
      setEmptyStars(amountOfEmptyStars);
    } else if (!halfStars && fullStars <= 4 && !emptyStars) {
      const amountOfEmptyStars = 5 - fullStars;
      console.log(amountOfEmptyStars);
      // setEmptyStars(amountOfEmptyStars);
    }
  }

  calculateAmountOfStars();

  console.log(fullStars, halfStars, emptyStars);

  return (
    <React.Fragment>
      <FontAwesomeIcon icon={faStar} />
      <FontAwesomeIcon icon={faStar} />
      <FontAwesomeIcon icon={faStar} />
      <FontAwesomeIcon icon={faStarHalfStroke} />
      <FontAwesomeIcon icon={faStarOutlined} />
    </React.Fragment>
  )
};

export default Stars;
