import React, { useContext } from "react";
import { CartContext } from "contexts/CartContext";
import { CartHandler, PriceHandler, products } from "config/C4";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";

const CartDrawer = ({ toggleCartDrawer }) => {
  const { cartItems } = useContext(CartContext);
  const { edit, remove } = CartHandler();

  return (
    <div className="cart-drawer">
      {/* <div className="cart-drawer__background" onClick={toggleCartDrawer} /> */}
      <div className="cart-drawer__content">
        <div className="cart-drawer__content--header">
          <button type="button" className="closeBtn" onClick={toggleCartDrawer}>
            <span className="material-icons">close</span>
          </button>
          <h1>
            Your Bag (
            {cartItems && cartItems.length > 0 ? cartItems.length : 0})
          </h1>
          {cartItems.length > 0 && (
            <h2>Your items are shipping for free!</h2>
          )}
        </div>
        <h1>Cart</h1>
        {cartItems.length > 0 ? (
          <div className="items">
            {cartItems.map((item, i) => {
              const product = products.filter(
                (p) => parseInt(p.id) === item.id
              )[0];
              return (
                <div key={i} className="cart__items--item">
                  <h3>
                    {product.title}
                    <span>[{item.amount}]</span>
                  </h3>
                  <FontAwesomeIcon
                    icon={faTimes}
                    onClick={() => remove(item.id)}
                  />

                  <button onClick={() => edit(item.id, "+")}>+</button>
                  <button
                    onClick={() => edit(item.id, "-")}
                    disabled={item.amount <= 1}
                  >
                    -
                  </button>

                  <h1>{PriceHandler(product.price * item.amount)}</h1>
                </div>
              );
            })}
          </div>
        ) : (
          <h1>Your cart is still empty!</h1>
        )}
      </div>
    </div>
  );
};

export default CartDrawer;
