import React from 'react';
import { Image, products } from '../../config/C4';

const ProductView = () => {
  const { pathname } = window.location;
  const slug = pathname.split('/product/')[1];
  const product = products.filter(i => i.url === slug);
  const { 
    type,
    title,
    url,
    id,
    price,
    fragrance,
    measurements,
    capacity,
    color,
    collection,
    featured_image,
    images,
    additionalInfo,
    additionalInfo: {
      short,
      description,
      ingredients,
    }
  } = product;

  return (
    <div className="productView">
      <div className="productView__header">
        <Image src={featured_image} alt={title} />
      </div>
    </div>
  )
}

export default ProductView;

[
    {
        "type": "candle",
        "title": "Lavender Skies Scented Candle",
        "url": "lavender-skies-scented-candle",
        "id": "1",
        "price": 2990,
        "fragrance": "",
        "measurements": "",
        "capacity": "360",
        "color": "white",
        "collection": "Private Collection",
        "featured_image": "lavender_skies_thumb.jpg",
        "images": [
            "lavender_skies_thumb.jpg",
            "lavender_skies_thumb.jpg"
        ],
        "additionalInfo": {
            "short": "luxurious scented candle, 360 gr",
            "description": [
                "Invite spring into your home with the Lavender Skies scented candle from our Private Collection. This composition is an experience of true floral sophistication. Pure violet and orris notes are lifted by distinct leathery accents and powerful woody aspects.",
                "The beautiful champagne coloured glass jar is a perfect addition to any decor, either in your own home or as a gift to another. When empty, clean out the candle and use it to hold flowers, pencils or brushes."
            ],
            "ingredients": [
                "Isoeugenol, Piperonal, 3-Methyl-4-(2,6,6-Trimethyl-2-Cyclohexen-1-yl)-3-Buten-2-One, Tetramethyl, Acetyloctahydronaphtalenes, Linalool, Coumarin."
            ]
        }
    }
]
