import React from 'react';
import PriceHandler from '../../config/PriceHandler';
import useCartHandler from '../../config/useCartHandler';

const ProductCard = (props) => {
    const { AddToCartHandler } = useCartHandler();
    const { item } = props;
    const { title, id, price, additionalInfo: { short }, featured_image } = item;

     return (
        <div className="product-card">
            <img src={require(`../../assets/img/${featured_image}`).default} alt="title" />
            <div className="product-card__text">
                <h1>{title}</h1>
                <p>{short}</p>
            </div>
            
            <PriceHandler price={price} />
            <button className="btn add-to-cart" onClick={() => AddToCartHandler(id)}>Add to cart</button>
        </div>
    )
}

export default ProductCard;
