import React from 'react';
import { Image, products } from '../../config/C4';

const ProductView = () => {
  const { pathname } = window.location;
  const slug = pathname.split('/product/')[1];
  const product = products.filter(i => i.url === slug)[0];
  const { 
    type,
    title,
    url,
    id,
    price,
    fragrance,
    measurements,
    capacity,
    color,
    collection,
    featured_image,
    images,
    additionalInfo,
  } = product;

  return (
    <div className="productView">
      <div className="productView__header">
        {product && <Image src={featured_image} alt={title} />}
      </div>
    </div>
  )
}

export default ProductView;
