import React, { Component } from 'react';
import './assets/sass/main.scss';
import CartContextProvider from './contexts/CartContext';
import {
  Nav,
  CartDrawer,
  HomeView,
} from './config/C4'

class App extends Component {
  constructor() {
    super();
    this.state = {
        cartDrawerActive: false,
    };

    this.toggleCartDrawer = this.toggleCartDrawer.bind(this);
  }

  async toggleCartDrawer() {
      const { cartDrawerActive } = this.state;
      this.setState({cartDrawerActive: !cartDrawerActive});

      if (!cartDrawerActive) {
          document.body.style.overflow = 'hidden';
      } else {
          document.body.style.overflow = 'auto';
      }
  }


  render() {
    const { cartDrawerActive } = this.state;
    return (
      <CartContextProvider>
        <Nav toggleCartDrawer={this.toggleCartDrawer}/>
        {cartDrawerActive && <CartDrawer toggleCartDrawer={this.toggleCartDrawer} />}
        <HomeView />
      </CartContextProvider>
    );
}
}

export default App;
