import products from "../../data/products";

const CalculateCartPrice = () => {
    const cartItems = JSON.parse(localStorage.getItem('cart'));
    let fullCartItems = cartItems.map(item => {
        const product = products.filter(product => {
            return product.id === item.id;
        })
        return (product[0].price * item.amount);
    });
    const totalPrice = fullCartItems.reduce((partial_sum, a) => partial_sum + a,0);
    localStorage.setItem('totalCartPrice', totalPrice);
}

export default CalculateCartPrice;
